import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { login } from "../api";

export default function Login() {
  const [email, setEmail] = useState(""); const [password, setPassword] = useState(""); const [error, setError] = useState(""); const navigate = useNavigate();

  async function handleSubmit(e) {
    e.preventDefault(); setError(""); const res = await login({ email, password });
    if (res.error) return setError(res.error);
    localStorage.setItem("bw_token", res.token);
    navigate("/");
  }

  return (
    <div style={{maxWidth:420, margin:"40px auto"}}>
      <div className="card-panel">
        <h4>Login</h4>
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <input className="input" placeholder="Email" value={email} onChange={e=>setEmail(e.target.value)} />
          </div>
          <div className="mb-3">
            <input type="password" className="input" placeholder="Password" value={password} onChange={e=>setPassword(e.target.value)} />
          </div>
          {error && <div className="text-danger mb-2">{error}</div>}
          <button className="btn-primary" type="submit">Login</button>
        </form>
      </div>
    </div>
  );
}
