import React, { useState } from "react";
import "./styles.css";

function App() {
  const [members, setMembers] = useState(["Daphne", "Mary"]);
  const [newMember, setNewMember] = useState("");
  const [expenses, setExpenses] = useState([]);
  const [payer, setPayer] = useState("");
  const [amount, setAmount] = useState("");
  const [description, setDescription] = useState("");

  const addMember = () => {
    if (newMember.trim() !== "") {
      setMembers([...members, newMember]);
      setNewMember("");
    }
  };

  const addExpense = () => {
    if (payer && amount && description) {
      setExpenses([
        ...expenses,
        { payer, amount: parseFloat(amount), description },
      ]);
      setAmount("");
      setDescription("");
    }
  };

  const total = expenses.reduce((acc, e) => acc + e.amount, 0);
  const split = members.length > 0 ? (total / members.length).toFixed(2) : 0;

  return (
    <div className="app-container">
      <h1>Smart Bill Splitter</h1>

      <div className="section">
        <h2>Members</h2>
        <div className="input-row">
          <input
            type="text"
            placeholder="Add new member"
            value={newMember}
            onChange={(e) => setNewMember(e.target.value)}
          />
          <button onClick={addMember}>+ Add</button>
        </div>
        <p>{members.join(", ")}</p>
      </div>

      <div className="section">
        <h2>Add Expense</h2>
        <div className="input-row">
          <input
            type="text"
            placeholder="Expense Description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
          <select
            value={payer}
            onChange={(e) => setPayer(e.target.value)}
          >
            <option value="">Select Payer</option>
            {members.map((m, i) => (
              <option key={i} value={m}>
                {m}
              </option>
            ))}
          </select>
          <input
            type="number"
            placeholder="Amount (₹)"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
          />
          <button onClick={addExpense}>Add</button>
        </div>

        <div className="expense-list">
          {expenses.map((exp, i) => (
            <div key={i} className="expense-item">
              <span>{exp.description}</span>
              <span>
                {exp.payer} paid ₹{exp.amount}
              </span>
            </div>
          ))}
        </div>
      </div>

      <div className="section">
        <h2>Summary</h2>
        <p>Total Spent: ₹{total.toFixed(2)}</p>
        <p>Each person should pay: ₹{split}</p>
      </div>
    </div>
  );
}

export default App;
