import React, { useState } from "react";
import Sidebar from "./components/Sidebar";
import "./styles.css";

export default function App() {
  const [groups, setGroups] = useState(["College Trip"]);
  const [activeGroup, setActiveGroup] = useState("College Trip");

  const addGroup = (name) => {
    if (!name.trim() || groups.includes(name)) return;
    setGroups([...groups, name.trim()]);
  };

  return (
    <div className="app">
      <Sidebar
        groups={groups}
        addGroup={addGroup}
        activeGroup={activeGroup}
        setActiveGroup={setActiveGroup}
      />

      {/* ✅ Main Dashboard Content */}
      <div className="main-content">
        <div className="section people-section">
          <h2>People</h2>
          <input type="text" placeholder="Name (e.g., Ayesha)" />
          <button className="add-btn">+ Add</button>

          <ul>
            <li>Ayesha</li>
            <li>Daphne</li>
            <li>Mary</li>
          </ul>
        </div>

        <div className="section expense-section">
          <h2>Expenses</h2>
          <input type="text" placeholder="Title (e.g., Dinner)" />
          <div className="expense-inputs">
            <select>
              <option>Select payer</option>
            </select>
            <input type="number" placeholder="Amount (₹)" />
          </div>

          <p>Participants:</p>
          <label><input type="checkbox" /> Ayesha</label>
          <label><input type="checkbox" /> Daphne</label>
          <label><input type="checkbox" /> Mary</label>
          <br />
          <button className="add-btn">+ Add Expense</button>
        </div>

        <div className="section results-section">
          <h2>Results</h2>
          <p>Ayesha: ₹0.00</p>
          <p>Daphne: ₹0.00</p>
          <p>Mary: ₹0.00</p>
          <h3>Settlement Plan</h3>
        </div>
      </div>
    </div>
  );
}
