import React, { useState } from "react";
import { Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  LineElement,
  CategoryScale,
  LinearScale,
  PointElement,
  Tooltip,
  Legend,
} from "chart.js";
ChartJS.register(LineElement, CategoryScale, LinearScale, PointElement, Tooltip, Legend);

const Dashboard = () => {
  const [members, setMembers] = useState([]);
  const [newMember, setNewMember] = useState("");
  const [expenses, setExpenses] = useState([]);
  const [desc, setDesc] = useState("");
  const [amount, setAmount] = useState("");
  const [payer, setPayer] = useState("");
  const [splitBetween, setSplitBetween] = useState([]);
  const [theme, setTheme] = useState("light");

  const addMember = () => {
    if (newMember.trim() !== "" && !members.includes(newMember)) {
      setMembers([...members, newMember]);
      setNewMember("");
    }
  };

  const toggleSplit = (member) => {
    if (splitBetween.includes(member)) {
      setSplitBetween(splitBetween.filter((m) => m !== member));
    } else {
      setSplitBetween([...splitBetween, member]);
    }
  };

  const addExpense = () => {
    if (!desc || !amount || !payer || splitBetween.length === 0) return alert("Fill all fields!");
    const newExpense = { desc, amount: parseFloat(amount), payer, splitBetween };
    setExpenses([...expenses, newExpense]);
    setDesc("");
    setAmount("");
    setPayer("");
    setSplitBetween([]);
  };

  // Calculate balances
  const balances = {};
  members.forEach((m) => (balances[m] = 0));

  expenses.forEach((exp) => {
    const share = exp.amount / exp.splitBetween.length;
    balances[exp.payer] += exp.amount;
    exp.splitBetween.forEach((m) => (balances[m] -= share));
  });

  const chartData = {
    labels: members,
    datasets: [
      {
        label: "Balance (‚Çπ)",
        data: members.map((m) => balances[m] || 0),
        borderColor: theme === "dark" ? "#00bfa5" : "#007bff",
        backgroundColor: theme === "dark" ? "#00bfa530" : "#007bff30",
        tension: 0.4,
        fill: true,
      },
    ],
  };

  const logout = () => {
    localStorage.removeItem("user");
    window.location.href = "/login";
  };

  return (
    <div className={`dashboard ${theme}`}>
      {/* Sidebar */}
      <div className="sidebar">
        <h3>üåê Groups</h3>
        <input type="text" placeholder="Add Group..." />
        <button>+</button>
      </div>

      {/* Main Content */}
      <div className="main-content">
        {/* Header */}
        <div className="header">
          <h2>üí∞ Billwise</h2>
          <div className="header-actions">
            <span className="welcome-text">Welcome, Demo User!</span>
            <button className="theme-btn" onClick={() => setTheme(theme === "light" ? "dark" : "light")}>
              {theme === "light" ? "üåô Dark" : "üåû Light"}
            </button>
            <button className="logout-btn" onClick={logout}>Logout</button>
          </div>
        </div>

        {/* Main 3-column grid */}
        <div className="dashboard-grid">
          {/* üßç People Section */}
          <div className="card-panel">
            <h4>People</h4>
            <div style={{ display: "flex", gap: "10px" }}>
              <input
                className="input"
                placeholder="Name (e.g., Ayesha)"
                value={newMember}
                onChange={(e) => setNewMember(e.target.value)}
              />
              <button className="btn-primary" onClick={addMember}>Add</button>
            </div>
            <ol className="member-list">
              {members.map((m, i) => (
                <li key={i}>{m}</li>
              ))}
            </ol>
          </div>

          {/* üíµ Expense Section */}
          <div className="card-panel">
            <h4>Expenses</h4>
            <input
              className="input"
              placeholder="Title (e.g., Dinner)"
              value={desc}
              onChange={(e) => setDesc(e.target.value)}
            />
            <div style={{ display: "flex", gap: "10px" }}>
              <select className="input" value={payer} onChange={(e) => setPayer(e.target.value)}>
                <option value="">Select Payer</option>
                {members.map((m, i) => (
                  <option key={i} value={m}>
                    {m}
                  </option>
                ))}
              </select>
              <input
                className="input"
                placeholder="Amount (‚Çπ)"
                value={amount}
                onChange={(e) => setAmount(e.target.value)}
              />
            </div>

            {/* Split Between */}
            <div className="split-section">
              <p>Who should share this expense?</p>
              <div className="split-list">
                {members.map((m, i) => (
                  <label key={i}>
                    <input
                      type="checkbox"
                      checked={splitBetween.includes(m)}
                      onChange={() => toggleSplit(m)}
                    />{" "}
                    {m}
                  </label>
                ))}
              </div>
            </div>

            <button className="btn-primary" style={{ marginTop: "12px" }} onClick={addExpense}>
              + Add Expense
            </button>
          </div>

          {/* üìà Results Section */}
          <div className="card-panel">
            <h4>Results</h4>
            {members.map((m, i) => (
              <p key={i}>
                {m}: ‚Çπ{(balances[m] || 0).toFixed(2)}
              </p>
            ))}
            <h5>Settlement Plan</h5>
            <ul>
              {members.map((m, i) =>
                balances[m] < 0 ? (
                  <li key={i}>
                    {m} should pay ‚Çπ{Math.abs(balances[m]).toFixed(2)}
                  </li>
                ) : balances[m] > 0 ? (
                  <li key={i}>
                    {m} will receive ‚Çπ{balances[m].toFixed(2)}
                  </li>
                ) : (
                  <li key={i}>{m} is settled</li>
                )
              )}
            </ul>

            {/* üìä Chart */}
            <div className="chart-container">
              <Line data={chartData} />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
