import Sidebar from "./components/Sidebar";
import React, { useState } from "react";
import "./styles.css";

function App() {
  const [groups, setGroups] = useState(["College Trip"]);
  const [activeGroup, setActiveGroup] = useState("College Trip");

  const addGroup = (name) => {
    if (!name.trim() || groups.includes(name)) return;
    setGroups([...groups, name.trim()]);
  };

  return (
    <div className="app">
      <Sidebar
        groups={groups}
        addGroup={addGroup}
        activeGroup={activeGroup}
        setActiveGroup={setActiveGroup}
      />
      <div className="app-container">
        <div className="app-grid">
          {/* Your Dashboard, People, Expenses, Results Components */}
        </div>
      </div>
    </div>
  );
}

export default App;
