import React, { useState } from "react";
import "./App.css";

function App() {
  const [members, setMembers] = useState(["Daphne", "Mary"]);
  const [newMember, setNewMember] = useState("");
  const [expenses, setExpenses] = useState([]);
  const [desc, setDesc] = useState("");
  const [amount, setAmount] = useState("");
  const [payer, setPayer] = useState("");
  const [selectedMembers, setSelectedMembers] = useState([]);

  // ‚ûï Add member
  const addMember = () => {
    if (!newMember.trim() || members.includes(newMember)) return;
    setMembers([...members, newMember]);
    setNewMember("");
  };

  // ‚úÖ Handle "Split with" selection
  const toggleMemberSelection = (member) => {
    setSelectedMembers((prev) =>
      prev.includes(member)
        ? prev.filter((m) => m !== member)
        : [...prev, member]
    );
  };

  // ‚ûï Add expense
  const addExpense = () => {
    const amt = parseFloat(amount);
    if (!desc || !amt || !payer || selectedMembers.length === 0) return;

    setExpenses([
      ...expenses,
      { desc, amount: amt, payer, splitWith: selectedMembers },
    ]);

    setDesc("");
    setAmount("");
    setPayer("");
    setSelectedMembers([]);
  };

  // üßÆ Calculate balances
  const balances = members.map((m) => {
    let paid = expenses
      .filter((e) => e.payer === m)
      .reduce((sum, e) => sum + e.amount, 0);

    let owed = 0;
    expenses.forEach((e) => {
      if (e.splitWith.includes(m)) {
        owed += e.amount / e.splitWith.length;
      }
    });

    return { name: m, balance: parseFloat((paid - owed).toFixed(2)) };
  });

  // ‚öñÔ∏è Settlement Calculation
  const settlements = [];
  const creditors = balances
    .filter((b) => b.balance > 0)
    .sort((a, b) => b.balance - a.balance);
  const debtors = balances
    .filter((b) => b.balance < 0)
    .sort((a, b) => a.balance - b.balance);

  let i = 0,
    j = 0;
  while (i < creditors.length && j < debtors.length) {
    const amountToSettle = Math.min(creditors[i].balance, -debtors[j].balance);
    settlements.push({
      from: debtors[j].name,
      to: creditors[i].name,
      amount: amountToSettle.toFixed(2),
    });
    creditors[i].balance -= amountToSettle;
    debtors[j].balance += amountToSettle;
    if (Math.abs(creditors[i].balance) < 0.01) i++;
    if (Math.abs(debtors[j].balance) < 0.01) j++;
  }

  // üì• Download CSV
  const downloadCSV = () => {
    let csv = "Member,Balance (‚Çπ)\n";
    balances.forEach((b) => (csv += `${b.name},${b.balance}\n`));
    csv += `\nWho Pays Whom:\n`;
    settlements.forEach((s) => (csv += `${s.from} ‚Üí ${s.to},‚Çπ${s.amount}\n`));

    const blob = new Blob([csv], { type: "text/csv" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "billwise_summary.csv";
    a.click();
    URL.revokeObjectURL(url);
  };

  const totalSpent = expenses.reduce((sum, e) => sum + e.amount, 0);

  return (
    <div>
      <div className="header">
        <h1>üí∞ Smart Bill Splitter</h1>
        <p className="subtitle">
          Add members, record expenses, and view settlements instantly.
        </p>
      </div>

      <div className="app-container">
        {/* üë• Members */}
        <div className="card">
          <h2>Members</h2>
          <input
            type="text"
            placeholder="Add new member"
            value={newMember}
            onChange={(e) => setNewMember(e.target.value)}
          />
          <button onClick={addMember}>+ Add</button>
          <ol className="member-list">
            {members.map((m, i) => (
              <li key={i}>{m}</li>
            ))}
          </ol>
        </div>

        {/* üí∏ Add Expense */}
        <div className="card">
          <h2>Add Expense</h2>
          <input
            type="text"
            placeholder="Expense Description"
            value={desc}
            onChange={(e) => setDesc(e.target.value)}
          />
          <input
            type="number"
            placeholder="Amount (‚Çπ)"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
          />
          <select value={payer} onChange={(e) => setPayer(e.target.value)}>
            <option value="">Select Payer</option>
            {members.map((m, i) => (
              <option key={i} value={m}>
                {m}
              </option>
            ))}
          </select>

          {/* üßë‚Äçü§ù‚Äçüßë Select who shares the expense */}
          <div className="split-with">
            <p>Split with:</p>
            {members.map((m, i) => (
              <label key={i} className="checkbox-label">
                <input
                  type="checkbox"
                  checked={selectedMembers.includes(m)}
                  onChange={() => toggleMemberSelection(m)}
                />
                {m}
              </label>
            ))}
          </div>

          <button onClick={addExpense}>Add Expense</button>

          <div className="expense-list">
            {expenses.length === 0 ? (
              <p className="muted">No expenses yet</p>
            ) : (
              <ul>
                {expenses.map((e, i) => (
                  <li key={i}>
                    <strong>{e.desc}</strong> ‚Äî ‚Çπ{e.amount} paid by {e.payer}{" "}
                    (split between {e.splitWith.join(", ")})
                  </li>
                ))}
              </ul>
            )}
          </div>
        </div>

        {/* üìä Summary */}
        <div className="card">
          <h2>Summary</h2>
          <p>
            <strong>Total Spent:</strong> ‚Çπ{totalSpent.toFixed(2)}
          </p>

          <div className="balances">
            {balances.map((b, i) => (
              <p key={i}>
                {b.name}:{" "}
                <span
                  style={{
                    color: b.balance >= 0 ? "green" : "red",
                    fontWeight: 600,
                  }}
                >
                  ‚Çπ{b.balance}
                </span>
              </p>
            ))}
          </div>

          <h3 className="settle-title">üí± Who Pays Whom</h3>
          {settlements.length === 0 ? (
            <p className="muted">No dues currently</p>
          ) : (
            <ul className="settlement-list">
              {settlements.map((s, i) => (
                <li key={i}>
                  <strong>{s.from}</strong> ‚Üí <strong>{s.to}</strong> ‚Çπ{s.amount}
                </li>
              ))}
            </ul>
          )}

          <button className="download-btn" onClick={downloadCSV}>
            ‚¨áÔ∏è Download Summary
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
