import React, { useState } from "react";
import "./styles.css";
import { Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

// üí∞ Helper: Settlement Logic
function computeSettlement(people, expenses) {
  const totals = {};
  people.forEach((p) => (totals[p.name] = 0));

  expenses.forEach((exp) => {
    const share = exp.amount / exp.splitBetween.length;
    exp.splitBetween.forEach((person) => {
      totals[person] -= share;
    });
    totals[exp.payer] += exp.amount;
  });

  const creditors = [];
  const debtors = [];
  for (const name in totals) {
    const val = Math.round((totals[name] + 0.00001) * 100) / 100;
    if (val > 0) creditors.push({ name, amount: val });
    else if (val < 0) debtors.push({ name, amount: -val });
  }

  creditors.sort((a, b) => b.amount - a.amount);
  debtors.sort((a, b) => b.amount - a.amount);

  const settlements = [];
  let i = 0,
    j = 0;
  while (i < creditors.length && j < debtors.length) {
    const take = Math.min(creditors[i].amount, debtors[j].amount);
    settlements.push({
      from: debtors[j].name,
      to: creditors[i].name,
      amount: Math.round(take * 100) / 100,
    });
    creditors[i].amount -= take;
    debtors[j].amount -= take;
    if (creditors[i].amount < 0.01) i++;
    if (debtors[j].amount < 0.01) j++;
  }

  return { totals, settlements };
}

export default function Dashboard() {
  const [people, setPeople] = useState([]);
  const [personName, setPersonName] = useState("");
  const [expenses, setExpenses] = useState([]);
  const [title, setTitle] = useState("");
  const [amount, setAmount] = useState("");
  const [payer, setPayer] = useState("");
  const [splitBetween, setSplitBetween] = useState([]);
  const [theme, setTheme] = useState("light");

  function addPerson() {
    if (!personName.trim()) return;
    if (people.find((p) => p.name === personName.trim())) return;
    const newPeople = [...people, { name: personName.trim() }];
    setPeople(newPeople);
    setPersonName("");
    if (!payer) setPayer(personName.trim());
    setSplitBetween(newPeople.map((p) => p.name));
  }

  function addExpense() {
    const amt = parseFloat(amount);
    if (!title || !amt || !payer || splitBetween.length === 0) return;
    const exp = { title, amount: amt, payer, splitBetween };
    setExpenses((prev) => [...prev, exp]);
    setTitle("");
    setAmount("");
  }

  const { totals, settlements } = computeSettlement(people, expenses);

  function togglePerson(name) {
    setSplitBetween((prev) =>
      prev.includes(name)
        ? prev.filter((p) => p !== name)
        : [...prev, name]
    );
  }

  function downloadSettlement() {
    let text = "From,To,Amount\n";
    settlements.forEach((s) => (text += `${s.from},${s.to},‚Çπ${s.amount}\n`));
    const blob = new Blob([text], { type: "text/csv" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "settlement.csv";
    a.click();
    URL.revokeObjectURL(url);
  }

  const toggleTheme = () =>
    setTheme((prev) => (prev === "light" ? "dark" : "light"));

  // üìä Bar Chart Data
  const chartData = {
    labels: Object.keys(totals),
    datasets: [
      {
        label: "Net Balance (‚Çπ)",
        data: Object.values(totals),
        backgroundColor: Object.values(totals).map((v) =>
          v >= 0 ? "#4CAF50" : "#E53935"
        ),
        borderRadius: 8,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: {
        display: false,
      },
      title: {
        display: true,
        text: "Settlement Overview",
        color: theme === "light" ? "#333" : "#fff",
      },
    },
    scales: {
      x: {
        ticks: { color: theme === "light" ? "#333" : "#fff" },
      },
      y: {
        ticks: { color: theme === "light" ? "#333" : "#fff" },
      },
    },
  };

  return (
    <div className={`dashboard ${theme}`}>
      <aside className="sidebar">
        <h3>üí∞ BillWise</h3>
        <button className="theme-toggle" onClick={toggleTheme}>
          {theme === "light" ? "üåô Dark" : "‚òÄÔ∏è Light"}
        </button>
        <h4>üë• Members</h4>
        <div className="add-member">
          <input
            className="input"
            placeholder="Enter name"
            value={personName}
            onChange={(e) => setPersonName(e.target.value)}
          />
          <button className="btn-primary" onClick={addPerson}>
            + Add
          </button>
        </div>
        <ol className="member-list">
          {people.map((p, i) => (
            <li key={i}>{`${i + 1}. ${p.name}`}</li>
          ))}
        </ol>
      </aside>

      <main className="main-content">
        <div className="card-panel">
          <h5>üßæ Add Expense</h5>
          <input
            className="input mb"
            placeholder="Title (e.g., Lunch)"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
          <input
            className="input mb"
            placeholder="Amount (‚Çπ)"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
          />
          <select
            className="input mb"
            value={payer}
            onChange={(e) => setPayer(e.target.value)}
          >
            <option value="">Select who paid</option>
            {people.map((p) => (
              <option key={p.name}>{p.name}</option>
            ))}
          </select>

          <div className="split-section">
            <h6>Split Between:</h6>
            <div className="split-list">
              {people.map((p) => (
                <label key={p.name}>
                  <input
                    type="checkbox"
                    checked={splitBetween.includes(p.name)}
                    onChange={() => togglePerson(p.name)}
                  />
                  {p.name}
                </label>
              ))}
            </div>
          </div>

          <button className="btn-primary mt" onClick={addExpense}>
            + Add Expense
          </button>
        </div>

        <div className="card-panel">
          <h5>üìä Settlement Summary</h5>
          {settlements.length === 0 ? (
            <p className="small-muted">No transactions yet</p>
          ) : (
            <>
              <ul>
                {settlements.map((s, i) => (
                  <li key={i}>
                    <strong>{s.from}</strong> should pay{" "}
                    <strong>‚Çπ{s.amount}</strong> to <strong>{s.to}</strong>
                  </li>
                ))}
              </ul>
              <div className="chart-container">
                <Bar data={chartData} options={chartOptions} />
              </div>
            </>
          )}
          <button className="btn-gray mt" onClick={downloadSettlement}>
            ‚¨áÔ∏è Download Summary
          </button>
        </div>
      </main>
    </div>
  );
}
