import React, { useState } from "react";
import "./styles.css";

function App() {
  const [members, setMembers] = useState(["Daphne", "Mary"]);
  const [newMember, setNewMember] = useState("");
  const [expenses, setExpenses] = useState([]);
  const [desc, setDesc] = useState("");
  const [amount, setAmount] = useState("");
  const [payer, setPayer] = useState("");

  // ‚ûï Add member
  const addMember = () => {
    if (!newMember.trim() || members.includes(newMember)) return;
    setMembers([...members, newMember]);
    setNewMember("");
  };

  // ‚ûï Add expense
  const addExpense = () => {
    const amt = parseFloat(amount);
    if (!desc || !amt || !payer) return;
    setExpenses([...expenses, { desc, amount: amt, payer }]);
    setDesc("");
    setAmount("");
    setPayer("");
  };

  // üßÆ Calculate totals
  const totalSpent = expenses.reduce((sum, e) => sum + e.amount, 0);
  const share = members.length > 0 ? totalSpent / members.length : 0;

  const balances = members.map((m) => {
    const paid = expenses
      .filter((e) => e.payer === m)
      .reduce((sum, e) => sum + e.amount, 0);
    const balance = paid - share;
    return { name: m, balance: parseFloat(balance.toFixed(2)) };
  });

  // ‚öñÔ∏è Settlement Calculation
  const settlements = [];
  const creditors = balances.filter((b) => b.balance > 0).sort((a, b) => b.balance - a.balance);
  const debtors = balances.filter((b) => b.balance < 0).sort((a, b) => a.balance - b.balance);

  let i = 0, j = 0;
  while (i < creditors.length && j < debtors.length) {
    const amountToSettle = Math.min(creditors[i].balance, -debtors[j].balance);
    settlements.push({
      from: debtors[j].name,
      to: creditors[i].name,
      amount: amountToSettle.toFixed(2),
    });
    creditors[i].balance -= amountToSettle;
    debtors[j].balance += amountToSettle;
    if (Math.abs(creditors[i].balance) < 0.01) i++;
    if (Math.abs(debtors[j].balance) < 0.01) j++;
  }

  // üì• Download CSV
  const downloadCSV = () => {
    let csv = "Member,Balance (‚Çπ)\n";
    balances.forEach((b) => (csv += `${b.name},${b.balance}\n`));
    csv += `\nTotal Spent,‚Çπ${totalSpent.toFixed(2)}\nEach Person Should Pay,‚Çπ${share.toFixed(2)}\n\nWho Pays Whom:\n`;
    settlements.forEach((s) => (csv += `${s.from} ‚Üí ${s.to},‚Çπ${s.amount}\n`));

    const blob = new Blob([csv], { type: "text/csv" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "billwise_summary.csv";
    a.click();
    URL.revokeObjectURL(url);
  };

  return (
    <div>
      <div className="header">
        <h1>üí∞ Smart Bill Splitter</h1>
        <p className="subtitle">
          Add members, track expenses, and see who owes whom instantly!
        </p>
      </div>

      <div className="app-container">
        {/* üë• Members */}
        <div className="card">
          <h2>Members</h2>
          <input
            type="text"
            placeholder="Add new member"
            value={newMember}
            onChange={(e) => setNewMember(e.target.value)}
          />
          <button onClick={addMember}>+ Add</button>
          <div className="member-list">
            {members.join(", ") || "No members yet"}
          </div>
        </div>

        {/* üí∏ Add Expense */}
        <div className="card">
          <h2>Add Expense</h2>
          <input
            type="text"
            placeholder="Expense Description"
            value={desc}
            onChange={(e) => setDesc(e.target.value)}
          />
          <input
            type="number"
            placeholder="Amount (‚Çπ)"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
          />
          <select
            value={payer}
            onChange={(e) => setPayer(e.target.value)}
          >
            <option value="">Select Payer</option>
            {members.map((m, i) => (
              <option key={i} value={m}>
                {m}
              </option>
            ))}
          </select>
          <button onClick={addExpense}>Add Expense</button>

          <div className="expense-list">
            {expenses.length === 0 ? (
              <p className="muted">No expenses added yet</p>
            ) : (
              <ul>
                {expenses.map((e, i) => (
                  <li key={i}>
                    {e.desc} ‚Äî ‚Çπ{e.amount.toFixed(2)} (paid by {e.payer})
                  </li>
                ))}
              </ul>
            )}
          </div>
        </div>

        {/* üìä Summary */}
        <div className="card">
          <h2>Summary</h2>
          <p><strong>Total Spent:</strong> ‚Çπ{totalSpent.toFixed(2)}</p>
          <p><strong>Each person should pay:</strong> ‚Çπ{share.toFixed(2)}</p>

          <div className="balances">
            {balances.map((b, i) => (
              <p key={i}>
                {b.name}:{" "}
                <span
                  style={{
                    color: b.balance >= 0 ? "green" : "red",
                    fontWeight: 600,
                  }}
                >
                  ‚Çπ{b.balance}
                </span>
              </p>
            ))}
          </div>

          <h3 className="settle-title">üí± Settlement Plan</h3>
          {settlements.length === 0 ? (
            <p className="muted">No one owes anyone right now</p>
          ) : (
            <ul className="settlement-list">
              {settlements.map((s, i) => (
                <li key={i}>
                  <strong>{s.from}</strong> ‚Üí <strong>{s.to}</strong>: ‚Çπ{s.amount}
                </li>
              ))}
            </ul>
          )}

          <button className="download-btn" onClick={downloadCSV}>
            ‚¨áÔ∏è Download Full Summary
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
