import React, { useState } from "react";
import { Bar, Pie } from "react-chartjs-2";
import "bootstrap/dist/css/bootstrap.min.css";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from "chart.js";
import "../styles.css";

// Register chart components
ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

// -------------------- Helper Function --------------------
function computeSettlement(people, expenses) {
  const totals = {};
  people.forEach((p) => (totals[p.name] = 0));

  expenses.forEach((exp) => {
    const share = exp.amount / exp.participants.length;
    exp.participants.forEach((part) => {
      totals[part] -= share;
    });
    totals[exp.payer] += exp.amount;
  });

  const creditors = [];
  const debtors = [];
  for (const name in totals) {
    const val = Math.round((totals[name] + 0.00001) * 100) / 100;
    if (val > 0) creditors.push({ name, amount: val });
    else if (val < 0) debtors.push({ name, amount: -val });
  }

  creditors.sort((a, b) => b.amount - a.amount);
  debtors.sort((a, b) => b.amount - a.amount);

  const settlements = [];
  let i = 0,
    j = 0;
  while (i < creditors.length && j < debtors.length) {
    const take = Math.min(creditors[i].amount, debtors[j].amount);
    settlements.push({
      from: debtors[j].name,
      to: creditors[i].name,
      amount: Math.round(take * 100) / 100,
    });
    creditors[i].amount = Math.round(
      (creditors[i].amount - take) * 100
    ) / 100;
    debtors[j].amount = Math.round((debtors[j].amount - take) * 100) / 100;
    if (Math.abs(creditors[i].amount) < 0.01) i++;
    if (Math.abs(debtors[j].amount) < 0.01) j++;
  }

  return { totals, settlements };
}

// -------------------- Dashboard Component --------------------
export default function Dashboard() {
  const [people, setPeople] = useState([
    { name: "Ayesha" },
    { name: "Daphne" },
    { name: "Mary" },
  ]);
  const [personName, setPersonName] = useState("");
  const [expenses, setExpenses] = useState([]);
  const [title, setTitle] = useState("");
  const [amount, setAmount] = useState("");
  const [payer, setPayer] = useState("Ayesha");
  const [selectedParts, setSelectedParts] = useState(
    people.map((p) => p.name)
  );

  function addPerson() {
    if (!personName.trim()) return;
    if (people.find((p) => p.name === personName.trim())) return;
    const newPeople = [...people, { name: personName.trim() }];
    setPeople(newPeople);
    setPersonName("");
    setPayer(newPeople[0].name);
    setSelectedParts(newPeople.map((p) => p.name));
  }

  function addExpense() {
    const amt = parseFloat(amount);
    if (!title || !amt || !payer || selectedParts.length === 0) return;
    const exp = { title, amount: amt, payer, participants: [...selectedParts] };
    setExpenses((prev) => [...prev, exp]);
    setTitle("");
    setAmount("");
    setSelectedParts(people.map((p) => p.name));
  }

  const { totals, settlements } = computeSettlement(people, expenses);

  function toggleParticipant(name) {
    setSelectedParts((prev) =>
      prev.includes(name)
        ? prev.filter((p) => p !== name)
        : [...prev, name]
    );
  }

  function loadDemo() {
    const demoPeople = [
      { name: "Ayesha" },
      { name: "Daphne" },
      { name: "Mary" },
    ];
    const demoExpenses = [
      {
        title: "Dinner",
        amount: 1200,
        payer: "Ayesha",
        participants: ["Ayesha", "Daphne", "Mary"],
      },
      {
        title: "Taxi",
        amount: 300,
        payer: "Daphne",
        participants: ["Daphne", "Mary"],
      },
      {
        title: "Snacks",
        amount: 150,
        payer: "Mary",
        participants: ["Ayesha", "Mary"],
      },
    ];
    setPeople(demoPeople);
    setExpenses(demoExpenses);
    setSelectedParts(demoPeople.map((p) => p.name));
  }

  function downloadSettlement() {
    let text = "From,To,Amount\n";
    settlements.forEach(
      (s) => (text += `${s.from},${s.to},${s.amount}\n`)
    );
    const blob = new Blob([text], { type: "text/csv" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "settlement.csv";
    a.click();
    URL.revokeObjectURL(url);
  }

  // Chart Data
  const chartData = {
    labels: Object.keys(totals || {}),
    datasets: [
      {
        label: "Net Balance (â‚¹)",
        data: Object.values(totals || {}),
        backgroundColor: ["#4caf50", "#2196f3", "#ff9800", "#f44336"],
        borderColor: "#fff",
        borderWidth: 1,
      },
    ],
  };

  const pieData = {
    labels: Object.keys(totals || {}),
