import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "../styles.css"; // your custom styles

// -------------------- Helper Function --------------------
function computeSettlement(people, expenses) {
  const totals = {};
  people.forEach((p) => (totals[p.name] = 0));

  expenses.forEach((exp) => {
    const share = exp.amount / exp.participants.length;
    exp.participants.forEach((part) => {
      totals[part] -= share;
    });
    totals[exp.payer] += exp.amount;
  });

  const creditors = [];
  const debtors = [];
  for (const name in totals) {
    const val = Math.round((totals[name] + 0.00001) * 100) / 100;
    if (val > 0) creditors.push({ name, amount: val });
    else if (val < 0) debtors.push({ name, amount: -val });
  }

  creditors.sort((a, b) => b.amount - a.amount);
  debtors.sort((a, b) => b.amount - a.amount);

  const settlements = [];
  let i = 0,
    j = 0;
  while (i < creditors.length && j < debtors.length) {
    const take = Math.min(creditors[i].amount, debtors[j].amount);
    settlements.push({
      from: debtors[j].name,
      to: creditors[i].name,
      amount: Math.round(take * 100) / 100,
    });
    creditors[i].amount = Math.round(
      (creditors[i].amount - take) * 100
    ) / 100;
    debtors[j].amount = Math.round((debtors[j].amount - take) * 100) / 100;
    if (Math.abs(creditors[i].amount) < 0.01) i++;
    if (Math.abs(debtors[j].amount) < 0.01) j++;
  }

  return { totals, settlements };
}

// -------------------- Dashboard Component --------------------
export default function Dashboard() {
  const [people, setPeople] = useState([
    { name: "Ayesha" },
    { name: "Daphne" },
    { name: "Mary" },
  ]);
  const [personName, setPersonName] = useState("");
  const [expenses, setExpenses] = useState([]);
  const [title, setTitle] = useState("");
  const [amount, setAmount] = useState("");
  const [payer, setPayer] = useState("Ayesha");
  const [selectedParts, setSelectedParts] = useState(
    people.map((p) => p.name)
  );

  function addPerson() {
    if (!personName.trim()) return;
    if (people.find((p) => p.name === personName.trim())) return;
    const newPeople = [...people, { name: personName.trim() }];
    setPeople(newPeople);
    setPersonName("");
    setPayer(newPeople[0].name);
    setSelectedParts(newPeople.map((p) => p.name));
  }

  function addExpense() {
    const amt = parseFloat(amount);
    if (!title || !amt || !payer || selectedParts.length === 0) return;
    const exp = { title, amount: amt, payer, participants: [...selectedParts] };
    setExpenses((prev) => [...prev, exp]);
    setTitle("");
    setAmount("");
    setSelectedParts(people.map((p) => p.name));
  }

  const { totals, settlements } = computeSettlement(people, expenses);

  function toggleParticipant(name) {
    setSelectedParts((prev) =>
      prev.includes(name)
        ? prev.filter((p) => p !== name)
        : [...prev, name]
    );
  }

  function loadDemo() {
    const demoPeople = [
      { name: "Ayesha" },
      { name: "Daphne" },
      { name: "Mary" },
    ];
    const demoExpenses = [
      {
        title: "Dinner",
        amount: 1200,
        payer: "Ayesha",
        participants: ["Ayesha", "Daphne", "Mary"],
      },
      {
        title: "Taxi",
        amount: 300,
        payer: "Daphne",
        participants: ["Daphne", "Mary"],
      },
      {
        title: "Snacks",
        amount: 150,
        payer: "Mary",
        participants: ["Ayesha", "Mary"],
      },
    ];
    setPeople(demoPeople);
    setExpenses(demoExpenses);
    setSelectedParts(demoPeople.map((p) => p.name));
  }

  function downloadSettlement() {
    let text = "From,To,Amount\n";
    settlements.forEach(
      (s) => (text += `${s.from},${s.to},${s.amount}\n`)
    );
    const blob = new Blob([text], { type: "text/csv" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "settlement.csv";
    a.click();
    URL.revokeObjectURL(url);
  }

  return (
    <>
      {/* Navbar */}
      <div className="navbar">
        <h3>ðŸ’¸ BillWise Dashboard</h3>
        <button onClick={() => alert("Logged out successfully!")}>
          Logout
        </button>
      </div>

      {/* Dashboard Grid */}
      <div className="app-grid">
        {/* --- People Panel --- */}
        <div className="card-panel">
          <h5>ðŸ‘¥ People</h5>
          <div className="d-flex gap-2 mb-2">
            <input
              className="input"
              placeholder="Name (e.g. Ayesha)"
              value={personName}
              onChange={(e) => setPersonName(e.target.value)}
            />
            <button className="btn-primary" onClick={addPerson}>
              + Add
            </button>
          </div>
          <div>
            {people?.length > 0 ? (
              people.map((p) => (
                <div key={p.name} style={{ padding: "6px 0" }}>
                  {p.name}
                </div>
              ))
            ) : (
              <div className="small-muted">No people added yet</div>
            )}
          </div>
        </div>

        {/* --- Expenses Panel --- */}
        <div className="card-panel">
          <h5>ðŸ’° Expenses</h5>
          <input
            className="input mb-2"
            placeholder="Title (e.g. Dinner)"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
          <div className="d-flex gap-2 mb-2">
            <select
              className="input"
              value={payer || ""}
              onChange={(e) => setPayer(e.target.value)}
            >
              {people.map((p) => (
                <option key={p.name} value={p.name}>
                  {p.name}
                </option>
              ))}
            </select>
            <input
              className="input"
              placeholder="Amount (â‚¹)"
              value={amount}
              onChange={(e) => setAmount(e.target.value)}
            />
          </div>

          <div style={{ marginTop: 10 }}>Participants:</div>
          <div className="d-flex flex-wrap gap-3 mt-2">
            {people.map((p) => (
              <label key={p.name} className="form-check-label">
                <input
                  type="checkbox"
                  checked={selectedParts.includes(p.name)}
                  onChange={() => toggleParticipant(p.name)}
                />{" "}
                {p.name}
              </label>
            ))}
          </div>

          <div className="d-flex gap-2 mt-3">
            <button className="btn-primary" onClick={addExpense}>
              + Add Expense
            </button>
            <button
              className="btn-gray"
              onClick={() => {
                setExpenses([]);
                setPeople([]);
              }}
            >
              Clear All
            </button>
            <button
              style={{
                background: "#07c6e6",
                color: "#000",
                padding: "10px 14px",
                borderRadius: 8,
                border: "none",
              }}
              onClick={loadDemo}
